using System;

namespace LongestPalindrome
{
    class Program
    {
        static void Main(string[] args)
        {
            Console.Write(LongestPalindrome(""));
        }

        public static string LongestPalindrome(string s) {
            string result = s[0].ToString();
            for (int i = 0; i < s.Length; i++)
            {
                for (int j = s.Length-1; j > i; j--)
                {
                    var ss = s.Substring(i, j-i+1);
                    if(IsPalindrome(ss))
                    {
                        if(ss.Length>result.Length)
                        {
                            result = ss;
                            break;
                        }
                    }
                }
                if(result.Length>= s.Length-i-1){
                    return result;
                }
            }
            return result;
        }

        public static bool IsPalindrome(string s){
            var length = s.Length;
            for (int i = 0; i < s.Length/2; i++)
            {
                if(s[i]!=s[length-i-1]){
                    return false;
                }
            }
            return true;
        }
    }
}
